'use strict';

var request;
try {
  request = require('superagent');
} catch (err) {
  request = superagent;
}

describe('File', function() {
  describe('Saving base64', function() {
    var base64 = 'd29ya2luZyBhdCBhdm9zY2xvdWQgaXMgZ3JlYXQh';
    var fileName = '中文.txt';

    it('should be saved', function() {
      var file = new AV.File(fileName, { base64: base64 });
      file.metaData('format', 'txt file');
      file.setACL(new AV.ACL());
      return file.save().then(function() {
        expect(file.id).to.be.ok();
        expect(file.metaData('format')).to.be('txt file');
        expect(file.get('mime_type')).to.be('text/plain');
        return file.destroy({ useMasterKey: true });
      });
    });

    it('with keepFileName', function() {
      var file = new AV.File(fileName, { base64: base64 });
      file.metaData('format', 'txt file');
      file.setACL(new AV.ACL());
      return file.save({ keepFileName: true }).then(function() {
        expect(file.url()).to.match(
          new RegExp(encodeURIComponent(fileName) + '$')
        );
        expect(file.ownerId()).to.be.ok();
        expect(file.id).to.be.ok();
        expect(file.metaData('format')).to.be('txt file');
        expect(file.get('mime_type')).to.be('text/plain');
        return file.destroy({ useMasterKey: true });
      });
    });
  });

  describe('Saving base64 image', function() {
    it('should be saved', function(done) {
      var base64 =
        '';
      var file = new AV.File('base64.gif', {
        base64: base64,
      });
      file.save().then(function(data) {
        var url = data.url();
        // check image url has image data.
        request('get', url).end(function(err, res) {
          if (res) {
            var data = res.body || res.text;
            expect(String(data).indexOf('GIF89a\u0018\u0000\u0018')).not.to.be(
              -1
            );
            done();
          } else {
            done(err);
          }
          file.destroy({ useMasterKey: true });
        });
      });
    });
  });

  describe('Test withURL', function() {
    it('should be saved', function() {
      var url =
        'http://i1.wp.com/blog.avoscloud.com/wp-content/uploads/2014/05/screen568x568-1.jpg?resize=202%2C360';
      var file = AV.File.withURL('screen.jpg', url);
      return file.save().then(function() {
        expect(file.ownerId()).to.be.ok();
        expect(file.id).to.be.ok();
        expect(file.metaData('__source')).to.be('external');
      });
    });
  });

  if (typeof Buffer !== 'undefined') {
    describe('Saving buffer in node.js', function() {
      it('should be saved', function() {
        var file = new AV.File('buffer-test', new Buffer('hello world'));
        return file.save().then(function() {
          expect(file.size()).to.be(11);
          expect(file.ownerId()).to.be.ok();
          expect(file.id).to.be.ok();
          return file.destroy({ useMasterKey: true });
        });
      });
    });

    describe('Saving stream in node.js', function() {
      if (process.env.S3 === 'true') {
        it('should throw with s3', () => {
          return new AV.File(
            'stream-test',
            require('fs').createReadStream(__filename)
          )
            .save()
            .should.be.rejectedWith(/not yet supported/);
        });
      } else {
        it('should be saved', function() {
          var file = new AV.File(
            'stream-test',
            require('fs').createReadStream(__filename)
          );
          return file.save().then(function() {
            expect(file.ownerId()).to.be.ok();
            expect(file.id).to.be.ok();
            return file.destroy({ useMasterKey: true });
          });
        });
      }
    });
  }

  describe('Saving array', function() {
    it('should be saved', function() {
      var bytes = [108, 101, 97, 110, 99, 108, 111, 117, 100];
      var file = new AV.File('myfile.txt', bytes);
      return file
        .save()
        .then(function() {
          expect(file.size()).to.be(9);
          expect(file.ownerId()).to.be.ok();
          expect(file.id).to.be.ok();
          return new Promise(function(resolve, reject) {
            request(file.url()).end(function(err, res) {
              if (err) {
                return reject(err);
              }
              resolve(res);
            });
          });
        })
        .then(function(res) {
          expect(res.text).to.be('leancloud');
          return file.destroy({ useMasterKey: true });
        });
    });
  });

  describe('Saving file with object', function() {
    it('should be saved', function() {
      var bytes = [108, 101, 97, 110, 99, 108, 111, 117, 100];
      var file = new AV.File('myfile.txt', bytes);
      return file.save().then(function() {
        var jobApplication = new AV.Object('JobApplication');
        jobApplication.set('applicantName', 'Joe Smith');
        jobApplication.set('applicantResumeFile', file);
        return jobApplication.save().then(function(result) {
          expect(result.id).to.be.ok();
          var query = new AV.Query('JobApplication');
          return query.get(result.id).then(function(ja) {
            expect(ja.id).to.be.ok();
            var arf = ja.get('applicantResumeFile');
            expect(arf).to.be.ok();
            expect(arf.size()).to.be(9);
            expect(arf.ownerId()).to.be.ok();
            return file.destroy({ useMasterKey: true });
          });
        });
      });
    });
  });

  describe('#fetch', function() {
    const fileId = process.env.FILE_ID || '52f9dd5ae4b019816c865985';
    it('createWithoutData() should return a File', function() {
      var file = AV.File.createWithoutData(fileId);
      expect(file).to.be.a(AV.File);
      expect(file.id).to.be(fileId);
    });
    it('save a fetched file should throw', function() {
      var file = AV.File.createWithoutData(fileId);
      expect(function saveFetchedFile() {
        file.save();
      }).to.throwError(/File is already saved\./);
    });
    describe('fetch', () => {
      before(function() {
        return AV.File.createWithoutData(fileId)
          .fetch()
          .then(file => (this.file = file));
      });
      it('should retrieve all data', function() {
        var file = this.file;
        expect(file).to.be.a(AV.File);
        expect(file.id).to.be(fileId);
        expect(file.name()).to.be('myfile.txt');
        expect(file.get('mime_type')).to.be('text/plain');
        expect(typeof file.url()).to.be('string');
      });
      it('decode and encode', function() {
        const json = this.file.toJSON();
        json.should.have.properties(['objectId', 'name', 'url']);
        const file = AV._decode(this.file._toFullJSON());
        expect(file).to.be.a(AV.File);
        expect(file.id).to.be(fileId);
        expect(file.name()).to.be('myfile.txt');
        expect(file.get('mime_type')).to.be('text/plain');
        expect(typeof file.url()).to.be('string');
      });
    });
  });

  describe('File get and set', function() {
    it('should be equal', function() {
      var base64 = 'd29ya2luZyBhdCBhdm9zY2xvdWQgaXMgZ3JlYXQh';
      var file = new AV.File('myfile.txt', { base64: base64 });
      file.set('format', 'txt file');
      file.set('name', 'tttt');
      return file.save().then(function() {
        expect(file.url()).to.be(file.get('url'));
        expect(file.name()).to.be('tttt');
        expect(file.metaData()).to.be(file.get('metaData'));
        expect(file.get('objectId')).to.be(file.id);
        expect(file.get('metaData').format).to.be('txt file');
        expect(file.get('createdAt')).to.be(file.createdAt);
        expect(file.get('updatedAt')).to.be(file.updatedAt);

        return file.destroy({ useMasterKey: true });
      });
    });
  });
});
